//// SPDX-License-Identifier: MIT
pragma solidity >=0.5.0 <0.7.0;

import "./BT_Token.sol";

contract Payment {
    string public name = "Payment";
    BT_Token public token;
    uint public rate = 100;   

    /*struct pending{
        uint amountrem;
        uint amountpaid;
        string tradeId;
        bool isPaid;
    }

    mapping(address => pending) public amountPending;*/

    struct pendings{
        uint amount;
        address sender;
        address reciever;
    }

    //string means the tradeId
    mapping(string => pendings) amountPending; 

    mapping(address => uint) public amountBlocked; 

    event Transfer30(
        address from,
        uint amount
    ); 

    constructor(BT_Token _token) public {
        token = _token;
    }    

    function transfer30(uint _amount, string memory _tId, address _reciever) public {
        //User can't sell more tokens than they have
        require(token.balanceOf(msg.sender) >= _amount, "Insufficient user balance");

        //Calculate the amount of Ether to redeem
        uint transferAmount = (_amount * 30 ) / 100;

        amountBlocked[msg.sender] = (_amount * 70 ) / 100;

        amountPending[_tId] = pendings({
            amount: _amount,
            sender: msg.sender,
            reciever: _reciever
        });
        //Perform sale
        token.transferFrom(msg.sender, address(this), transferAmount);
        //This function transfers the etherAmount to the msg.sender
        //msg.sender.transfer(etherAmount);

        //Emit an event
        emit Transfer30(msg.sender, transferAmount);
    }

    function transfer70(string memory _tradeId) public {
        require(token.balanceOf(msg.sender) >= amountPending[_tradeId].amount, "Insufficient balance");

        //transferring 70% from sender to reciever
        token.transferFrom(amountPending[_tradeId].sender, amountPending[_tradeId].reciever, ((amountPending[_tradeId].amount * 70) / 100));

        //Transfer 30% from admin wallet
        token.transferFrom(msg.sender, amountPending[_tradeId].reciever, ((amountPending[_tradeId].amount * 70) / 100));
    }
}
