//// SPDX-License-Identifier: MIT
pragma solidity >=0.5.0 <0.7.0;

import "./BT_Token.sol";

contract Payment {
    string public name = "Payment";
    BT_Token public token;
    uint public rate = 100;   

    mapping(address => uint) public amountBlocked; 

    event Transfer30(
        address from,
        uint amount
    ); 

    constructor(BT_Token _token) public {
        token = _token;
    }    

    function transfer30(uint _amount) public {
        //User can't sell more tokens than they have
        require(token.balanceOf(msg.sender) >= _amount, "Insufficient user balance");

        //Calculate the amount of Ether to redeem
        uint transferAmount = (_amount * 30 ) / 100;

        amountBlocked[msg.sender] = (_amount * 70 ) / 100;
        //Perform sale
        token.transferFrom(msg.sender, address(this), transferAmount);
        //This function transfers the etherAmount to the msg.sender
        //msg.sender.transfer(etherAmount);

        //Emit an event
        emit Transfer30(msg.sender, transferAmount);
    }
}
