//// SPDX-License-Identifier: MIT
pragma solidity >=0.5.0 <0.7.0;


contract Trades {

    string[] public tradeId;
    uint public count;

    struct Deal {
        string tradeHash;
        bool isConfirmed;    
    }

    Deal[] deals;

    mapping(string => Deal) tradeList;

    function setTrade(string memory _id, string memory _tHash) public {

        tradeId.push(_id);

        deals.push(Deal({
            tradeHash: _tHash,
            isConfirmed: false
        }));

        tradeList[_id] = Deal({
            tradeHash: _tHash,
            isConfirmed: false
        });

        count++;
    }   

    function getTrade(string memory _id) public view returns(string memory _name){ 

        return (tradeList[_id].tradeHash);
    } 

    function getStatus(string memory _id) public view returns(bool _confirmation){

    	return (tradeList[_id].isConfirmed);
    }

}
